						}
					}
					ry=ry+k;
				}else if(dir.equals("S")){
					for(int j=1;j<=k;j++){
						rob[rx][ry-j]++;
						if(rob[rx][ry+j]==gem[rx][ry-j]){
							last++;
							gem[rx][ry-j]=0;
						}
					}
					ry=ry-k;
				}else if(dir.equals("E")){
					for(int j=1;j<=k;j++){
						rob[rx+j][ry]++;
						if(rob[rx+j][ry]==gem[rx+j][ry]){
							last++;
							gem[rx+j][ry]=0;
						}
					}
