			nx=ny=10;
			for(int i=0; i<m; i++){

				String dir=sc.next();//direction
				int move=sc.nextInt();
				//ここから移動処理。どうやら入力で範囲をはみ出ることはないらしいが、一応そこのチェックを書いておいた。
				//途中でめんどくさくなってmoveの範囲外の処理は書かなかった。
				//処理順が気持ち悪いのは方角と方向の対応を勘違いしてたため。
				if(dir.equals("W")){
					for(int j=0; j<=move; j++){
						if(0 <= ny-j){
							map[ny-j][nx]=false;
						}
					}
					ny-=move;
				}
				else if(dir.equals("E")){
					for(int j=0; j<=move; j++){
						if(nx+j < 21){
							map[ny][nx+j]=false;
						}
					}
					nx+=move;
				}
				else if(dir.equals("N")){
					for(int j=0; j<=move; j++){
						if(ny+j < 21){
							map[ny+j][nx]=false;
						}
					}
					ny+=move;
				}
				else if(dir.equals("S")){
					for(int j=0; j<=move; j++){
						if(0 <= nx-j){
							map[ny][nx-j]=false;
						}
					}
					nx-=move;
				}
//				System.out.println("ny="+ny+" nx="+nx);
			}
			//complet check
			boolean f=false;;
			for(int i=0; i<21; i++){
				for(int j=0; j<21; j++){
					if(map[i][j]){
						f=true;
						break;
					}
				}
			}
			if(f) System.out.println("No");
			else System.out.println("Yes");
		}

	}

}

import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner sc = new Scanner(System.in);
        int w = 20;

        int[][] edo = new int[w+1][w+1];

        while (true) {
            for (int y = 0; y <= w; ++y) {
                for (int x = 0; x <= w; ++x) {
                    edo[y][x] = 0;
                }
            }

            int n = sc.nextInt();
            if (n == 0) break;

            for (int i = 0; i < n; ++i) {
                int x, y;
                x = sc.nextInt();
                y = sc.nextInt();
                edo[y][x]++;
            }

            int m = sc.nextInt();

            int px = 10, py = 10, cnt = 0;
            for (int i = 0; i < m; ++i) {
                String v = sc.next();
                int d = sc.nextInt();
                int ny = py+d, sy = py-d, ex = px+d, wx = px-d;
                if (v.equals("N")) {
                    for (int y = py; y <= ny; ++y) {
                        if (edo[y][px] > 0) {
                            cnt += edo[y][px];
                            edo[y][px] = 0;
                        }
                    }
                    py += d;
                }
                else if (v.equals("S")) {
                    for (int y = py; y >= sy; --y) {
                        if (edo[y][px] > 0) {
                            cnt += edo[y][px];
                            edo[y][px] = 0;
                        }
                    }
                    py -= d;
                }
                else if (v.equals("E")) {
                    for (int x = px; x <= ex; ++x) {
                        if (edo[py][x] > 0) {
                            cnt += edo[py][x];
                            edo[py][x] = 0;
                        }
                    }
                    px += d;
                }
                else if (v.equals("W")) {
                    for (int x = px; x >= wx; --x) {
                        if (edo[px][x] > 0) {
                            cnt += edo[px][x];
                            edo[px][x] = 0;
                        }
                    }
                    px -= d;
                }
            }
            if (cnt == n) {
                System.out.println("YES");
            }
            else {
                System.out.println("NO");
            }
        }

    }
}

