		Scanner in = new Scanner(System.in);
		while(true){
			int n = in.nextInt();
			if(n==0)break;
			int field[][] = new int[limit][limit];
			for (int i = 0; i < limit ; i++) {
				for (int j = 0; j < limit; j++) {
					field[i][j] = 0;
				}
			}
			for (int i = 0; i < n; i++) {
				int x = in.nextInt();
