				doGet(robot,diamond);
				if(robot.x == 0 || i == 0) break;
				robot.x -= 1; 
			}
		}

		if(mes == false){
			for(int i = 0; i <= length; i++){
				doGet(robot,diamond);
				if(robot.y == 19 || i == length) break;
				robot.y += 1; 
			}
			for(int i = length; i < 0; i++){
				doGet(robot,diamond);
				if(robot.y == 0 || i == 0) break;
				robot.y -= 1; 
			}
		}
	}

	static void doGet(Point robot, Point[] diamond){
		for(int i = 0; i < N; i++){
			if(diamond[i].x == robot.x && diamond[i].y == robot.y){
				diamond[i].get = true;
			}
		}
	}
}

class Point{
	int x;
	int y;
	boolean get;

	Point(int x, int y){
		this.x = x;
		this.y = y;
		get = false;
	}
}

