import java.util.Scanner;

public class Main {
	public static void main(String[] args) {

		Scanner s = new Scanner(System.in);

		while (true) {
			int N = s.nextInt();

			if (N == 0)
				break;

			int[][] graph = new int[21][21];

			for (int i = 0; i < N; i++) {
				int x = s.nextInt();
				int y = s.nextInt();
				graph[y][x] = 1;
			}

			// for (int p = 0; p < 21; p++) {
			// for (int q = 0; q < 21; q++) {
			// System.out.print(graph[p][q]);
			// }
			// System.out.println();
			// }

			int M = s.nextInt();
			int robox = 10, roboy = 10;

			for (int j = 0; j < M; j++) {
				char d = s.next().charAt(0);
				int l = s.nextInt();

				graph[roboy][robox] = 2;

				switch (d) {
				case 'N':
					for (int t = 0; t <= l; t++) {
						graph[roboy + t][robox] = 2;
					}
					roboy = roboy + l;
					break;

				case 'E':
					for (int t = 0; t <= l; t++) {
						graph[roboy][robox + t] = 2;
					}
					robox = robox + l;
					break;

				case 'W':
					for (int t = 0; t <= l; t++) {
						graph[roboy][robox - t] = 2;
					}
					robox = robox - l;
					break;

				case 'S':
					for (int t = 0; t <= l; t++) {
						graph[roboy - t][robox] = 2;
					}
					roboy = roboy - l;
					break;

				default:
					break;

				}

				// for (int p = 0; p < 21; p++) {
				// for (int q = 0; q < 21; q++) {
				// System.out.print(graph[p][q]);
				// }
				// System.out.println();
				// }

			}

			int count = 0;

			for (int p = 0; p < 21; p++) {
				for (int q = 0; q < 21; q++) {
					if (graph[p][q] == 1)
						count++;
				}
			}

			if (count == 0)
				System.out.println("Yes");
			else
				System.out.println("No");

		}
		s.close();
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main{
	public static void main(String[] args)throws IOException{
		InputStreamReader isr = new InputStreamReader(System.in);
		BufferedReader reader = new BufferedReader(isr);
		Main app = new Main();
		String string;

		int n, x, y, hoge;
		char flag;
		while(true){
			n = Integer.valueOf(reader.readLine());
			if(n == 0) break;

			boolean map[][] = new boolean[21][21];
			for(int i = 0; i < n; i++){
				string = reader.readLine();
				x = Integer.valueOf(string.split(" ")[0]);
				y = 20 - Integer.valueOf(string.split(" ")[1]);

				map[y][x] = true;
			}

			n = Integer.valueOf(reader.readLine());
			x = y = 10;
			for(int i = 0;  i < n; i++){
				string = reader.readLine();
				flag = string.charAt(0);
				hoge = Integer.valueOf(string.split(" ")[1]);

				switch(flag){
				case 'N' : 
					for(int j = 0; j < hoge; j++){
						--y;
						if(map[y][x]) map[y][x] = false;
					}
					break;
				case 'E' : 
					for(int j = 0; j < hoge; j++){
						++x;
						if(map[y][x]) map[y][x] = false;
					}
					break;
				case 'S' : 
					for(int j = 0; j < hoge; j++){
						++y;
						if(map[y][x]) map[y][x] = false;
					}
					break;
				case 'W' :
					for(int j = 0; j < hoge; j++){
						--x;
						if(map[y][x]) map[y][x] = false;
					}
				}
			}
			if(app.judge(map)){
				System.out.println("Yes");
			}else{
				System.out.println("No");
			}
		}
		reader.close();
	}

	public boolean judge(boolean map[][]){
		boolean res = true;
		for(int i = 0; i < map.length; i++){
			for(int j = 0; j < map[i].length; j++){
				if(map[i][j]){
					res = false;
				}
			}
			if(!res) break;
		}
		return res;
	}
}

import java.util.Scanner;

public class Main {
	public static void main(String[] args) {

		int numOfGemsCollected = 0;

		Robo robo = new Robo();

		Scanner scan = new Scanner(System.in);
		int numOfGems = scan.nextInt();

		while (numOfGems != 0) {
			Gem[] gemArray = new Gem[numOfGems];

			for (int i = 0; i < numOfGems; i++) {
				Gem gem = new Gem();
				int xOfGem = scan.nextInt();
				int yOfGem = scan.nextInt();

				gem.setX(xOfGem);
				gem.setY(yOfGem);

				gemArray[i] = gem;
			}

			//動く回数を読み込み
			int numOfMoves = scan.nextInt();

			//動く回数分だけ、方向読み込み→距離読み込みを繰り返し
			for (int m = 0; m < numOfMoves; m++) {
				String direction = scan.next();
				int distance = scan.nextInt();

				//東ならX座標を＋１
				if (direction.equals("E")) {
					for (int j = 0; j <= distance; j++) {
						robo.forwardX();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
							}
						}
					}
				}

				//西ならX座標をー１
				if (direction.equals("W")) {
					for (int j = 0; j < distance; j++) {
						robo.backwardX();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
							}
						}
					}
				}

				//北ならY座標を＋１
				if (direction.equals("N")) {
					for (int j = 0; j < distance; j++) {
						robo.forwardY();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
							}
						}
					}
				}

				//南ならY座標をー１
				if (direction.equals("S")) {
					for (int j = 0; j < distance; j++) {
						robo.backwardY();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
							}
						}
					}
				}
			}

			if (numOfGems == numOfGemsCollected) {
				System.out.println("Yes");
			} else {
				System.out.println("No");
			}
		}
	}
}

