public class Main {
    public static void main(String args[]){
        char direction;
        int N, M, robot_x, robot_y, distance, count;
        int x[] = new int[20];
        int y[] = new int[20];
        String line;
        Scanner sc = new Scanner(System.in);
        while(true){
            N = sc.nextInt();
            if(N == 0) break;
            for(int i = 0; i < N; i++){
                x[i] = sc.nextInt();
                y[i] = sc.nextInt();
            }
            M = sc.nextInt();
            count = 0;
            robot_x = 10;
            robot_y = 10;
            for(int i = 0; i < M; i++) {
                line = sc.next();
                distance = sc.nextInt();
                direction = line.charAt(0);
                while (distance > 0) {
                    switch (direction) {
                        case 'N':
                            robot_y++;
                            break;
                        case 'E':
                            robot_x++;
