import java.io.*;
import java.util.StringTokenizer;
import java.util.Arrays;

class Robot {
	int x,y;
	Robot(int x,int y,int[][] field) {
		this.x = x;
		this.y = y;
		field[x][y] = 0;
	}
	void move(int dir,int size,int[][] field) {
		if (dir==0) {
			for (int i=0;i<size;i++) {
				if (field[this.x][--this.y]==1) field[this.x][this.y] = 0;
			}
		} else if (dir==1) {
			for (int i=0;i<size;i++) {
				if (field[this.x][++this.y]==1) field[this.x][this.y] = 0;
			}
		} else if (dir==2) {
			for (int i=0;i<size;i++) {
				if (field[--this.x][this.y]==1) field[this.x][this.y] = 0;
			}
		} else if (dir==3) {
			for (int i=0;i<size;i++) {
				if (field[++this.x][this.y]==1) field[this.x][this.y] = 0;
			}
		}
	}
}

