						for(int k = 0; k < stone.length; k++){
							if( stone[k][0] == now[0] && stone[k][1] == now[1] ){
								stone[k][0] = -1;
								stone[k][1] = -1;
								count++;
							}
						}
					}
				} else if( direction[i].equals("W")){
					for(int j = 1; j <= distance[i]; j++){
						now[0]--;
						for(int k = 0; k < stone.length; k++){
							if( stone[k][0] == now[0] && stone[k][1] == now[1] ){
								stone[k][0] = -1;
								stone[k][1] = -1;
								count++;
							}
						}
					}
				}
				if( count == n ){
