		Scanner sc = new Scanner(System.in);
		while(sc.hasNext()){
			int n = sc.nextInt();
			if(n==0)break;

			for(int i=0;i<=20;i++) {
				for(int j=0;j<=20;j++)
					f[i][j] = 0;
			}
			boolean ans = true;
			for(int i=0;i<n;i++) {
				int x = sc.nextInt();
				int y = sc.nextInt();
				f[x][y] = 1;
			}

			int m = sc.nextInt();
			int x=10;
			int y=10;
			for(int i=0;i<m;i++) {
				char a = sc.next().charAt(0);
				int d = sc.nextInt();
				switch(a) {
				case 'N':
					for(int k=y;k<=y+d;k++) {	
						f[x][k] = 0;
					}
					y += d;
					break;
				case 'E':
					for(int k=x;k<=x+d;k++) {
						f[k][y] = 0;
					}
					x += d;
					break;
				case 'S':
					for(int k=y;k>=y-d;k--) {
						f[x][k] =0;
					}
					y -= d;
					break;
				case 'W':
					for(int k=x;k>=x-d;k--) {
						f[k][y] =0;
					}
					x -= d;
					break;
				}
			}

			for(int i=0;i<=20;i++) {
				for(int j=0;j<=20;j++)
					if(f[i][j] != 0) {
						ans = false;
						break;
					}
				if(!ans)break;
			}

			if(ans)System.out.println("Yes");
			else System.out.println("No");

		}

	}
}

import java.util.Scanner;

class Robo {
	int coordinateX;
	int coordinateY;

	Robo() {
		this.coordinateX = 10;
		this.coordinateY = 10;
	}

	public void forwardX() {
		this.coordinateX = (this.getX() + 1);
	}

	public void forwardY() {
		this.coordinateY = (this.getY() + 1);
	}

	public void backwardX() {
		this.coordinateX = (this.getX() - 1);
	}

	public void backwardY() {
		this.coordinateY = (this.getY() - 1);
	}

	public int getX() {
		return this.coordinateX;
	}

	public int getY() {
		return this.coordinateY;
	}
}

class Gem {
	int coordinateX;
	int coordinateY;

	public void setX(int x) {
		this.coordinateX = x;
	}

	public void setY(int y) {
		this.coordinateY = y;
	}

	public int getX() {
		return this.coordinateX;
	}

	public int getY() {
		return this.coordinateY;
	}
}

public class Main {
	public static void main(String[] args) {

		Scanner scan = new Scanner(System.in);

		while (true) {

			int numOfGems = scan.nextInt();
			if (numOfGems == 0) {
				break;
			}

			int numOfGemsCollected = 0;
			Robo robo = new Robo();

			Gem[] gemArray = new Gem[numOfGems];

			for (int i = 0; i < numOfGems; i++) {
				Gem gem = new Gem();
				int xOfGem = scan.nextInt();
				int yOfGem = scan.nextInt();

				gem.setX(xOfGem);
				gem.setY(yOfGem);

				gemArray[i] = gem;
			}

			//動く回数を読み込み
			int numOfMoves = scan.nextInt();

			//動く回数分だけ、方向読み込み→距離読み込みを繰り返し
			for (int m = 0; m < numOfMoves; m++) {
				String direction = scan.next();
				int distance = scan.nextInt();

				//東ならX座標を＋１
				if (direction.equals("E")) {
					for (int j = 0; j < distance; j++) {
						robo.forwardX();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
								gemArray[i] = null;
							}
						}
					}
				}

				//西ならX座標をー１
				if (direction.equals("W")) {
					for (int j = 0; j < distance; j++) {
						robo.backwardX();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
								gemArray[i] = null;
							}
						}
					}
				}

				//北ならY座標を＋１
				if (direction.equals("N")) {
					for (int j = 0; j < distance; j++) {
						robo.forwardY();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
								gemArray[i] = null;
							}
						}
					}
				}

				//南ならY座標をー１
				if (direction.equals("S")) {
					for (int j = 0; j < distance; j++) {
						robo.backwardY();
						for (int i = 0; i < numOfGems; i++) {
							if ((robo.getX() == gemArray[i].getX()) && (robo.getY() == gemArray[i].getY())) {
								numOfGemsCollected++;
								gemArray[i] = null;
							}
						}
					}
				}
			}

			if (numOfGems == numOfGemsCollected) {
				System.out.println("Yes");
			} else {
				System.out.println("No");
			}
		}
		scan.close();
	}
}

