			dy['S'] = -1;
			dx['E'] = 1;
			dx['W'] = -1;

			int cx = 10;
			int cy = 10;

			boolean[] visited = new boolean[n];
			for (int i = 0; i < n; i++) {
				char ch = sc.next().charAt(0);
				int distance = sc.nextInt();

				for (int ii = 0; ii < distance; ii++) {
					cx += dx[ch];
					cy += dy[ch];

					for(int j = 0; j < n ; j++){
