    public class Robot
    {
        private Field field;
        private int x;
        private int y;

        public Robot(Field field)
        {
            x = 10;
            y = 10;
            this.field = field;
        }

        public void Operate(String way, int distance)
        {
            for (int i = 0; i < distance; i++)
            {
                FindGem();
                switch (way)
                {
                    case "N":
                        y ++;
                        break;
                    case "E":
                        x ++;
                        break;
                    case "S":
                        y --;
                        break;
                    case "W":
                        x--;
                        break;
                }
                FindGem();
            }
        }

        public bool IsComplete()
        {
            return field.IsComplete();
        }

        private void FindGem()
        {
            field.FindGem(x, y);
        }


    }

    public class Field
    {
        private bool[,] gemsMap;

        private const int SIZE = 21;

        public Field()
        {
            gemsMap = new bool[SIZE, SIZE];
        }

        public void FindGem(int x, int y)
        {
            gemsMap[x, y] = false;
        }

        public void CreateGem(int x, int y)
        {
            gemsMap[x, y] = true;
        }

        public bool IsComplete()
        {
            for (int y = 0; y < SIZE; y++)
            {
                for (int x = 0; x < SIZE; x++)
                {
                    if (gemsMap[x, y]) return false;
                }
            }
            return true;
        }
    }
}

using System;
