namespace AOJ_ICPC1 {
	struct Coo {
		public int x, y;
		public Coo(int _x, int _y) {
			x = _x; y = _y;
		}
		public void Move(Operation.Direction d) {
			switch (d) {
				case Operation.Direction.N: y++; break;
				case Operation.Direction.S: y--; break;
				case Operation.Direction.E: x++; break;
				case Operation.Direction.W: x--; break;
			}
		}
	}
	class Operation {
		public enum Direction{N, E, S, W};
		public Direction dir; 
		public int move;
		public Operation(char c, int m) {
			switch (c) {
				case 'N': dir = Direction.N; break;
				case 'E': dir = Direction.E; break;
				case 'S': dir = Direction.S; break;
				case 'W': dir = Direction.W; break;
			}
			move = m;
		}
	}
	class Program {
		static void Main(string[] args) {

			while (true) {
				int N = int.Parse(Console.ReadLine());
				if (N == 0) break;
				List<Coo> cooList = new List<Coo>();
				for (int n = 0; n < N; n++) {
					string[] str = Console.ReadLine().Split(' ');
					cooList.Add(new Coo(int.Parse(str[0]), int.Parse(str[1])));
				}
				int M = int.Parse(Console.ReadLine());
				Operation[] op = new Operation[M];
				for (int m = 0; m < M; m++) {
					string[] str = Console.ReadLine().Split(' ');
					op[m] = new Operation(str[0][0], int.Parse(str[1]));
				}


				Console.WriteLine(result(cooList, op)?"Yes":"No");
			}
		}

		static bool result(List<Coo> coolist, Operation[] op) {
			Coo pos = new Coo(10, 10);
			foreach (var o in op) {
				while (o.move > 0) {
					pos.Move(o.dir);
					o.move--;
					coolist.RemoveAll(c => c.x == pos.x && c.y == pos.y);
				}
			}
			return coolist.Count == 0;
		}
	}
}

