        {
            int n, m;
            int[][] jem, move;
            int x, y;
            int count = 0;
            n = sc.NextInt();
            jem = new int[n][];
            if (n == 0)
            {
                q.Answer();
                break;
            }
            for (int i = 0; i < n; i++)
            {
                jem[i] = sc.NextIntArray();
            }
