            South,
            East,
            West
        }
        struct Position
        {
            public int X;
            public int Y;
            public Position(int x, int y)
            {
                X = x;
                Y = y;
            }
            public static Position operator+ (Position pos1, Position pos2)
            {
                return new Position(pos1.X + pos2.X, pos1.Y + pos2.Y);
            }
        }
        class JewelryMap
        {
            private int maxX = 21;
            private int maxY = 21;
            private bool[,] map;

            public int Count
            {
                get;
                protected set;
            }

            public JewelryMap()
            {
                map = new bool[maxY, maxX];
            }
            public void SetJewelry(Position pos)
            {
                if (map[pos.Y, pos.X]) return;

                map[pos.Y, pos.X] = true;
                Count++;
            }
            public bool GetJewelry(Position pos)
            {
                if (!map[pos.Y, pos.X]) return false;

                map[pos.Y, pos.X] = false;
                Count--;

                return true;
            }
            public Position SpawnPosition()
            {
                return new Position(maxY / 2, maxX / 2);
            }
            public void Reset()
            {
                Count = 0;
                map.Initialize();
            }
        }
        class MyRobot
        {
            public Position Position
            {
                get;
                protected set;
            }
            public int Count
            {
                get;
                protected set;
            }
            protected JewelryMap Map
            {
                get;
                set;
            }

            public MyRobot(JewelryMap map)
            {
                Position = map.SpawnPosition();
                Map = map;
            }
            public void Move(Direction dir, int value)
            {
                Position vec = new Position(0,0);
                switch(dir)
                {
                    case Direction.North:   vec.Y = 1;  break;
                    case Direction.South:   vec.Y = -1; break;
                    case Direction.East:    vec.X = 1;  break;
                    case Direction.West:    vec.X = -1; break;
                }
                for(var i = 0; i < value; i++)
                {
                    Position += vec;
                    Check();
                }
            }
            protected void Check()
            {
                if(Map.GetJewelry(Position)) Count++;
            }
            public void Reset()
            {
                Position = Map.SpawnPosition();
                Count = 0;
            }
        }
    }
}

