        for(int y = 0; y < Consts.Y_SIZE; y++){
            for(int x = 0; x < Consts.X_SIZE; x++){
                Map[y,x] = false;
            }
        }

        // Gem Input to Map
        for(int i = 0; i < GemNum; i++){
            string[] GemX_Y = Console.ReadLine().Split(' ');
            Map[int.Parse(GemX_Y[1]),int.Parse(GemX_Y[0])] = true;
