            Commands.Add(new Command());
            Commands[i].Init(V_D);
        }
    }

    public int Solve(){
        int BotX = Consts.InitBotX;
        int BotY = Consts.InitBotY;
        int dx = 0;
        int dy = 0;
        int CorrectedGemNum = 0;
        for(int i = 0; i < Commands.Count; i++){
            switch(Commands[i].Vector){
