            int[][] jem, move;
            int x, y;
            int count = 0;
            n = sc.NextInt();
            jem = new int[n][];
            if (n == 0)
            {
                q.Answer();
                break;
            }
            for (int i = 0; i < n; i++)
            {
                jem[i] = sc.NextIntArray();
            }
            m = sc.NextInt();
            move = new int[m][];
            for (int i = 0; i < m; i++)
            {
                move[i] = new int[2];
                switch (sc.NextString())
                {
                    case "N":
                        move[i][1] = sc.NextInt();
                        continue;
                    case "E":
                        move[i][0] = sc.NextInt();
                        continue;
                    case "S":
                        move[i][1] = -sc.NextInt();
                        continue;
                    case "W":
                        move[i][0] = -sc.NextInt();
                        continue;
                    default:
                        continue;
                }
            }
            x = y = 10;
            for (int i = 0; i < m; i++)
            {
                x += move[i][0];
                y += move[i][1];
                int xmax = Math.Max(x, x - move[i][0]);
                int xmin = Math.Min(x, x - move[i][0]);
                int ymax = Math.Max(y, y - move[i][1]);
                int ymin = Math.Min(y, y - move[i][1]);
                for (int j = 0; j < n; j++)
                {
                    if ( ( xmax >= jem[j][0] && xmin <= jem[j][0])
                      && (ymax >= jem[j][1] && ymin <= jem[j][1]) )
                    {
                        count++;
                        jem[j][0] = -1; //重複して宝石を拾わないようにする
                        jem[j][1] = -1;
                    }
                }
            }

            q.Enqueue(count == n ? "YES" : "NO");
        }
        q.Answer();
    }
}

