        {

            for(int i = 0; i < jwel_point.Length; i++)
            {
                int x = jwel_point[i].x;
                int y = jwel_point[i].y;

                if(before.x - after.x == 0)
                {
                    if(before.y < y && y <= after.y && before.x == x)
                    {
                        get_jwel[i] = true;
                    }
                    else if(before.y > y && y >= after.y && before.x == x)
                    {
                        get_jwel[i] = true;
                    }
                }
                else
                {
                    if (before.x < x && x <= after.x && before.y == y)
                    {
                        get_jwel[i] = true;
                    }
                    else if (before.x > x && x >= after.x && before.y == y)
                    {
                        get_jwel[i] = true;
                    }
                }

            }

        }

