                    Point before_posi;
                    before_posi.x = rob_posi.x;
                    before_posi.y = rob_posi.y;

                    if(dire == "E")
                    {
                        rob_posi.x += amo;
                    }
                    else if(dire == "S")
                    {
                        rob_posi.y -= amo;
                    }
                    else if(dire == "W")
