        while (true)
        {
            var map = new int[21, 21];
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            for (int i = 0; i < n; i++)
            {
                string[] line = Console.ReadLine().Trim().Split(' ');
                map[int.Parse(line[0]), int.Parse(line[1])] = 1;
            }
            var gcount = 0;
            var p = new Po { x = 10, y = 10 };
