public class hello
{
    public static void Main()
    {
        var gems = new List<Gem>();

        while (true)
        {
            var gemgetcount = 0;
            gems.RemoveAll(x => x.gx == 100);
            var n = int.Parse(Console.ReadLine().Trim());
            if (n == 0) break;
            for (int i = 0; i < n; i++)
            {
                string[] line = Console.ReadLine().Trim().Split(' ');
                var x = int.Parse(line[0]);
                var y = int.Parse(line[1]);
                var a = new Gem() { gx = x, gy = y };
                gems.Add(a);
            }
            var m = int.Parse(Console.ReadLine().Trim());

            var rx = 10;
            var ry = 10;

            for (int i = 0; i < m; i++)
            {
                string[] line2 = Console.ReadLine().Trim().Split(' ');
                var k = int.Parse(line2[1]);
                switch (line2[0])
                {
                    case "N":
                        for (int j = 0; j < k; j++)
                        {
                            ry++;
                            if (GemCheck(gems, rx, ry))
                            {
                                gemgetcount++;
                                gems.Remove(new Gem() { gx = rx, gy = ry });
                            }
                        }
                        break;
                    case "E":
                        for (int j = 0; j < k; j++)
                        {
                            rx++;
                            if (GemCheck(gems, rx, ry))
                            {
                                gemgetcount++;
                                gems.Remove(new Gem() { gx = rx, gy = ry });
                            }
                        }
                        break;
                    case "S":
                        for (int j = 0; j < k; j++)
                        {
                            ry--;
                            if (GemCheck(gems, rx, ry))
                            {
                                gemgetcount++;
                                gems.Remove(new Gem() { gx = rx, gy = ry });
                            }
                        }
                        break;
                    case "W":
                        for (int j = 0; j < k; j++)
                        {
                            rx--;
                            if (GemCheck(gems, rx, ry))
                            {
                                gemgetcount++;
                                gems.Remove(new Gem() { gx = rx, gy = ry });
                            }
                        }
                        break;
                }
            }
            if (gemgetcount >= n) Console.WriteLine("Yes");
            else Console.WriteLine("No");
        }
    }
    public static bool GemCheck(List<Gem> ggg, int xx, int yy)
    {
        var res1 = ggg.Where(z => z.gx == xx).Where(zz => zz.gy == yy).Count();
        if (res1 == 1) return true;
        else return false;
    }
}

