def moveRobot(d,l,robotPosition,square):
    get_gems = 0
    for i in range(0,l):
        if d == "N":
            robotPosition["y"] = min(20, robotPosition["y"]+1) 
        elif d == "S":
            robotPosition["y"] = max(0, robotPosition["y"]-1) 
        elif d == "E":
            robotPosition["x"] = min(20, robotPosition["x"]+1) 
        elif d == "W":
            robotPosition["x"] = max(0, robotPosition["x"]-1)
        if square[robotPosition["x"]][robotPosition["y"]] == 1:
            square[robotPosition["x"]][robotPosition["y"]] = 0
            get_gems += 1
    return robotPosition,square,get_gems

