from collections import defaultdict

def check(D, STEP,  x, y):
    global moves_x, moves_y, oks

    if D == 'N':
        y += STEP
        for _y, i in moves_x.get(x, []):
            if y-STEP <= _y and _y <= y:
                oks[i] = 1
            else:
                continue
        return x, y
    elif D == 'S':
        y -= STEP
        for _y, i in moves_x.get(x, []):
            if y+STEP >= _y and _y >= y:
                oks[i] = 1
            else:
                continue
        return x, y
    elif D == 'E':
        x += STEP
        for _x, i in moves_y.get(y, []):
            if x-STEP <= _x and _x <= x:
                oks[i] = 1
            else:
                continue
        return x, y
    elif D == 'W':
        x -= STEP
        for _x, i in moves_y.get(y, []):
            if x+STEP >= _x and _x >= x:
                oks[i] = 1
            else:
                continue
        return x, y

