class Mission:
  def __init__(self):
    self.field = [[0]*21 for i in range(21)]
    self.x = 10
    self.y = 10

  def get_gem(self, x, y):
    self.field[x][y] = 0

  def put_gem(self, x, y):
    self.field[x][y] = 1

  def move_x(self, step):
    for i in range(1,step+1): self.get_gem(self.x+i, self.y)
    self.x += step

  def move_y(self, step):
    for j in range(1,step+1): self.get_gem(self.x, self.y+j)
    self.y += step

def main():
