      if direction == 'N': m.move_y(step)
      elif direction == 'E': m.move_x(step)
      elif direction == 'S': m.move_y(-step)
      elif direction == 'W': m.move_x(-step)

    if m.field == [[0]*21 for i in range(21)]: print 'Yes'
