_robotXY = []

def RobotMove(dir, amt, stPoint):
    global _robotXY

    isXMove = True

    orgX, orgY = _robotXY
    if dir == "N":
        isXMove = False
        _robotXY[1] += amt
    elif dir == "E":
        _robotXY[0] += amt
    elif dir == "S":
        isXMove = False
        _robotXY[1] -= amt
    elif dir == "W":
        _robotXY[0] -= amt

    # X軸移動
    if isXMove:
        startX = min(orgX, _robotXY[0])
        endX = max(orgX, _robotXY[0])
        for i in range(startX, endX + 1):
            if [i, _robotXY[1]] in stPoint:
                stPoint.remove([i, _robotXY[1]])

    # Y軸移動
    else:
        startY = min(orgY, _robotXY[1])
        endY = max(orgY, _robotXY[1])
        for i in range(startY, endY + 1):
            if [_robotXY[0], i] in stPoint:
                stPoint.remove([_robotXY[0], i])



