def set(Map, x, y) :
	for i in range(N) :
		Map[y[i]][x[i]] = 1
	return Map

def search(Map, dir, dis) :
	X = 10
	Y = 10
	for i in range(M) :
		if(dir[i] == "S") :
			if(Y + dis[i] <= 20) :
				for j in range(dis[i]+ 1) :
					Map[Y + j][X] = 0
				Y += dis[i]
			else :
				for j in range(20 - Y) :
					Map[Y + j][X] = 0
				Y = 20
		elif(dir[i] == "E") :
			if(X + dis[i] <= 20) :
				for j in range(dis[i]+ 1) :
					Map[Y][X + j] = 0
				X += dis[i]
			else :
				for j in range(20 - X) :
					Map[Y][X + j] = 0
				X = 20
		elif(dir[i] == "N") :
			if(Y - dis[i] >= 0) :
				for j in range(dis[i]+ 1) :
					Map[Y - j][X] = 0
				Y -= dis[i]
			else :
				for j in range(Y + 1) :
					Map[Y - j][X] = 0
				Y = 0
		else :
			if(X - dis[i] >= 0) :
				for j in range(dis[i]+ 1) :
					Map[Y][X - j] = 0
				X -= dis[i]
			else :
				for j in range(X + 1) :
					Map[Y][X - j] = 0
				X = 0
	return Map

