        self.gem_map.discard((self.x, self.y))
        for i in range(len(order)):
            direction, vector = order[i]
            for j in range(vector):
                if direction == "N":
                    self.y += 1
                elif direction == "S":
                    self.y -= 1
                elif direction == "E":
                    self.x += 1
                else:
                    self.x -= 1
                self.gem_map.discard((self.x, self.y))

