        distance = int(str_distance)
        axe, dir = {'N':[1, 1], 'S':[1, -1], 'E':[0, 1], 'W':[0, -1]}[direction]
        for dis in range(1, distance+1):
            self.robot[axe] += dir
            if self.jewels[self.robot[0]][self.robot[1]] == True:
                self.nj -= 1
                self.jewels[self.robot[0]][self.robot[1]] = False

    def judge(self):
        if self.nj == 0:
            return True
        else:
            return False

