def between(sx,sy,mx,my,ex,ey):
    # y軸方向への移動
    if sx == mx and mx == ex:
        if sy <= my and my <= ey:
            return True
        elif ey <= my and my <= sy:
            return True
    elif sy == my and my == ey:
        if sx <= mx and mx <= ex:
            return True
        elif ex <= mx and mx <= sx:
            return True
    return False

