use std::io::{stdin, Read, StdinLock};
use std::str::FromStr;

struct Scanner<'a> {
    cin : StdinLock<'a>,
}

impl<'a> Scanner<'a> {
    fn new(cin : StdinLock<'a>) -> Scanner<'a> {
        Scanner { cin: cin }
    }

    fn read1<T: FromStr>(&mut self) -> Option<T> {
        let token = self.cin.by_ref().bytes().map(|c| c.unwrap() as char)
            .skip_while(|c| c.is_whitespace())
            .take_while(|c| !c.is_whitespace())
            .collect::<String>();
        token.parse::<T>().ok()
    }

    fn read<T: FromStr>(&mut self) -> T {
        self.read1().unwrap()
    }
}

fn main(){
    let cin = stdin();
    let cin = cin.lock();
    let mut sc = Scanner::new(cin);
    loop {
        let n: usize = sc.read();
        if n == 0 { break; };
        let mut g = vec![vec![0; 21]; 21];
        for _ in 0..n {
            let x: usize = sc.read();
            let y: usize = sc.read();
            g[y][x] += 1;
        }
        let m: usize = sc.read();
        let mut cx = 10;
        let mut cy = 10;
        let mut cnt = 0;
        cnt += g[cy][cx];
        g[cy][cx] = 0;
        for _ in 0..m {
            let s: String = sc.read();
            let d: usize = sc.read();
            for _ in 0..d {
                match &*s {
                    "N" => { cy += 1; },
                    "E" => { cx += 1; },
                    "W" => { cx -= 1; },
                    "S" => { cy -= 1; },
                    _ => (),
                };
                cnt += g[cy][cx];
                g[cy][cx] = 0;
            }
        }
        println!("{}", if cnt == n { "Yes" } else { "No" });
    }
}
