  int min10 = 21, min50 = 21, min100 = 21, min500 = 21;
  int a10, a50, a100, a500;
  int sum = 84;  
  for ( int i = 0; i <= c10; i++ ) {
    for ( int j = 0; j <= c50; j++ ) {
      for ( int k = 0; k <= c100; k++ ) {
	for ( int l = 0; l <= c500; l++ ) {
	  int tmp = 10*i+50*j+100*k+500*l;
	  if ( paid > tmp ) continue;	  
	  int dif = tmp-paid;
	  int t10 = c10-i, t50 = c50-j, t100 = c100-k, t500 = c500-l;
