int main()
{
	int label[] = {10, 50, 100, 500};
	int num[4], price;
	bool first=true;
	while (cin>>price, price) {
		rep(i, 4) {
			cin>>num[i];
		}
		int ret = -price;
		rep(i, 4) {
			ret += num[i]*label[i];
		}
		int ans[4] = {0};
		while (ret>=500) {
			ret-=500;
			ans[3]++;
		}
		while (ret>=100) {
			ret-=100;
			ans[2]++;
		}
		while (ret>=50) {
			ret-=50;
			ans[1]++;
		}
		while (ret>=10) {
			ret-=10;
			ans[0]++;
		}
		if (!first)
			cout << endl;
		else
			first = false;

		rep(i, 4) {
			ans[i] = max(0, num[i]-ans[i]);
			if (ans[i] != 0)
				cout << label[i] << " " << ans[i] << endl;
		}
	}
}

