int main(void) {
    int money;
    const int coin[4] = {1, 5, 10, 50};
    int have[4];
    cin >> money;
    do {
        int pay[4] = {0};
        money /= 10;
        for (int i = 0; i < 4; i++) {
            cin >> have[i];
        }
        for (int i = 0; i < 4; i++) {
            if (money < coin[i]) {
                break; 
            } else {
                int max_pay = (money / coin[i]) + 1;
                //cout << "MAXPAY = " << max_pay << endl;
                //cout << "HAVE = " << have[i] << endl;
                if (have[i] >= max_pay) {
                    //cout << "If" << endl;
                    pay[i] += max_pay;      
                    money -= max_pay * coin[i];
                    have[i] -= max_pay;
                } else {
                    //cout << "Else" << endl;
                    pay[i] += have[i];
                    money -= have[i] * coin[i];
                    have[i] = 0;
                }
            }
        }
        output(pay);
        cin >> money;
        if (money == 0) {
            break;
        }
        cout << endl;
    } while (1);
    return 0;
}

