int n;  //
int coin[4];    //
int mcoin[4];
int m;
int mi;

int chng(int k, int bi){
    int cnt = 0; 
    while(k / 500){
        if(bi & (1<<3)) {
            //cout << " a " << k << " " << bi << endl;
            return INF;
        }
        k -= 500;
        cnt++;
    }
    while(k / 100){
        if(bi & (1<<2)) return INF;
        k -= 100;
        cnt++;
    }
    while(k / 50){
        if(bi & (1<<1)) return INF;
        k -= 50;
        cnt++;
    }
    while(k / 10){
        if(bi & (1<<0)) return INF;
        k -= 10;
        cnt++;
    }

    return cnt;
}

int main(){

    while(cin >> n, n){
        //input
        for(int i = 0; i < 4; i++) cin >> coin[i];
        mi = 1000000;
        int bi = 0;
        for(int a = 0; a <= coin[0]; a++){
            if(a) bi |= 1;
            for(int b = 0; b <= coin[1]; b++){
                if(b) bi |= 1<<1;
                else bi &= ~(1<<1);
                for(int c = 0; c <= coin[2]; c++){
                    if(c) bi |= 1<<2;
                    else bi &= ~(1<<2);
                    for(int d = 0; d <= coin[3]; d++){
                        if(d) bi |= 1<<3;
                        else bi &= ~(1<<3);
                        int sum;
                        if((sum = a*10 + b*50 + c*100 + d*500) >= n){
                            m = coin[0] + coin[1] + coin[2] + coin[3] - a - b - c - d;
                            m += chng(sum - n, bi);
                            if(mi > m){
                                mi = m;
                                mcoin[0] = a;
                                mcoin[1] = b;
                                mcoin[2] = c;
                                mcoin[3] = d;
                                //cout << mi << ' ' << a << ' ' << b << ' ' << c << ' ' << d << ' ' << endl;
                            }
                        }
                    }
                }
            }
        }


        //output
        if(mcoin[0])cout << 10 << ' ' << mcoin[0] << endl;
        if(mcoin[1])cout << 50 << ' ' << mcoin[1] << endl;
        if(mcoin[2])cout << 100 << ' ' << mcoin[2] << endl;
        if(mcoin[3])cout << 500 << ' ' << mcoin[3] << endl;
        cout << endl;
    }

    return 0;
}

