#include <iostream>
#define llint long long
#define inf 1e18

using namespace std;

llint n;
llint a, b, c, d;

int main(void)
{
	bool flag = true;
	while(1){
		cin >> n;
		if(n == 0) break;
		cin >> a >> b >> c >> d;

		llint ans = inf, aa, bb, cc, dd;
		for(int i = 0; i <= a; i++){
			for(int j = 0; j <= b; j++){
				for(int k = 0; k <= c; k++){
					for(int l = 0; l <= d; l++){
						llint x = l*500+k*100+j*50+i*10;
						if(x < n) continue;
						llint ex = x - n;
						llint A = ex / 500; ex %= 500;
						llint B = ex / 100; ex %= 100;
						llint C = ex / 50; ex %= 50;
						llint D = ex / 10;
						if(l*A || k*B || j*C || i*D) continue;

						llint tmp = (a-i)+(b-j)+(c-k)+(d-l)+A+B+C+D;
						if(ans > tmp){
							ans = tmp;
							aa = i, bb = j, cc = k, dd = l;
						}
					}
				}
			}
		}
		if(!flag) cout << endl;
		flag = false;
		if(aa > 0) cout << 10 << " " << aa << endl;
		if(bb > 0) cout << 50 << " " << bb << endl;
		if(cc > 0) cout << 100 << " " << cc << endl;
		if(dd > 0) cout << 500 << " " << dd << endl;
	}

	return 0;
}

