template<class T>struct InputC {
	operator T() { return InputF<T>(); }
	vector<T> operator[](int h) {
		vector<T> ret(h); rep(i, h)ret[i] = InputF<T>(); return ret;
	}
	vector<vector<T>> operator[](PII p) {
		int h, w; tie(h, w) = p; vector<vector<T>> ret(h, vector<T>(w));
		rep(i, h)rep(j, w)ret[i][j] = InputF<T>(); return ret;
	}
	T operator()(int n) {
		while (sz(InputB) <= n)InputB.pb(InputF<string>()); return Parse<T>(InputB[n]);
	}
	vector<T> operator()(int n, int h) {
		vector<T> ret(h); rep(i, h)ret[i] = operator()(i + n); return ret;
	}
	vector<vector<T>> operator()(int n, int h, int w) {
		vector<vector<T>> ret(h, vector<T>(w));
		rep(i, h)rep(j, w)ret[i][j] = operator()(n + i * w + j); return ret;
