using namespace std;

enum coins{ ten = 0, fifty, hundred, five_hundred, coin_max };
const int kcoin[coin_max] = {10,50,100,500};

int min_nums;
int ans_nums[coin_max];
int nums[coin_max];
int tans_nums[coin_max];

void solve(int m){
	if( m <= 0 ){
		int t = 0;
		for(int c = 0; c < coin_max-1; ++c){
			switch(c){
				case ten:
					if( nums[ten] >= 10 ){ nums[ten] -= 10; nums[hundred] += 1; tans_nums[ten] += 10; --c; }
					if( nums[ten] >= 5 ){  nums[ten] -= 5;  nums[fifty] += 1;   tans_nums[ten] += 5;  --c; }
					break;
				case fifty:
					if( nums[fifty] >= 10 ){ nums[fifty] -= 10; nums[five_hundred] += 1; tans_nums[fifty] += 10; --c; }
					if( nums[fifty] >= 2 ){  nums[fifty] -= 2;  nums[hundred] += 1;      tans_nums[fifty] += 2;  --c; }
					break;
				case hundred:
					if( nums[hundred] >= 5 ){ nums[hundred] -= 5; nums[five_hundred] += 1; tans_nums[hundred] += 5; --c; }
					break;
			}
		}
		for(int c = 0; c < coin_max; ++c){
			t += nums[c];
		}
		if( min_nums > t ){
			min_nums = t;
			for(int c = 0; c < coin_max; ++c){
				ans_nums[c] = tans_nums[c];
			}
		}
		return ;
	}

	for(int i = 0; i < coin_max; ++i){
		if( nums[i] == 0 )
			continue;
		--nums[i];
		++tans_nums[i];
		solve( m - kcoin[i] );
		++nums[i];
		--tans_nums[i];
	}
}

