			tans_nums[i] = ans_nums[i] = 0;
		}

		for(int i = 0; i < coin_max; ++i){
			scanf("%d", nums+i);
		}

		if( !bFirstDataSet )
			printf("\n");
		bFirstDataSet = false;

		solve( M );

