		return 10 * pow(10,(int)(ind/2));
	}
	else {
		return 50 * pow(10,(int)(ind/2));
	}
}

bool can_ex(std::vector<int> v, std::vector<int> m, int ind) {
	if (ind==0 || ind==3) {
		return false;
	}

	if (v[ind+1] == 0) {
		return true;
	}
	else {
		if (v[3] > 0) {
			return false;
		}

		int change;
		if (ind == 1) {
			change = m[ind]/2;
			if (change+m[ind+1] > 4) {
				return true;
			}
		}
	}

	return true;
}


int main(void) {
	int p;
	bool fn = false;
	cin >> p;

	while (p != 0) {
		int sum = 0;
		int change;
		int temp;
		std::vector<int> m(4,0);
		std::vector<int> pay(4);

		if (fn) {
			cout << endl;
		}
		else {
			fn = true;
		}

		for (int i = 0; i < 4; i++) {
			cin >> m[i];
		}

		for (int i = 0; sum < p; i++) {
			while (sum < p && m[i] > 0) {
				m[i]--;
				pay[i]++;
				sum += check(i);
			}
		}

		change = sum - p;

		for (int i = 0; i < 4; i++) {
			temp = change/check(3-i);
			if (change/check(3-i) > 0) {
				if (pay[3-i] > 0) {
					pay[3-i] -= temp;
					m[3-i] += temp;
				}
			}
			change = change%check(3-i);
		}

		temp = 0;

		for (int i = 0; i < 4; i++) {
			if (can_ex(pay,m,i)) {
				if (i == 1) {
					if (m[i] > 1) {
						temp = m[i]/2;
						m[i+1] += temp;
						pay[i] += m[i] - m[i]%2; 
						m[i] = m[i]%2;
					}
				}
				else {
					if (m[i] > 4) {
						m[i+1] += m[i]/5;
						pay[i] += m[i] - m[i]%5 - temp; 
						m[i] = m[i]%5;
					}
				}
			}

