typedef vector<int> sequence;

sequence shop(int value) {
  sequence seq(4);
  seq[0] = value / 500; value -= 500 * seq[0];
  seq[1] = value / 100; value -= 100 * seq[1];
  seq[2] = value / 50; value -= 50 * seq[2];
  seq[3] = value;
  return seq;
}

