    mn_count = kInf;
    rep(i,4) scanf("%d", coin+i);
    f(0,0,0);
    for (int i = 0; i < 4; ++i) {
      int counts = (mn_state >> (5 * i)) & 31;
      if (counts == 0) continue;
      printf("%d %d\n", pows[i], counts);
    }
    puts("");
  }
  return 0;
}

