		++res;
	}
	while(ret >= 100) {
		ret -= 100;
		++res;
	}
	while(ret >= 50) {
		ret -= 50;
		++res;
	}
	while(ret >= 10) {
		ret -= 10;
		++res;
	}
	return res;
}


