void returnCharge(int n, int pay[4], int coin[4], int have[4], int ans[6]){
    int payTotal = 0;
    rep(i,4) payTotal+= pay[i] * coin[i];
    int charge = payTotal - n;

    for(int i = 3; i >= 0; i--){
        have[i]+= charge / coin[i]; 
        charge = charge % coin[i];
    }

    int totalHaveCoin = 0,totalPayCoin = 0;
    rep(i,4){
        totalHaveCoin += have[i];
        totalPayCoin += pay[i];
    }
    if( (totalHaveCoin < ans[4]) || (totalHaveCoin == ans[4] && totalPayCoin < ans[5])){
        rep(i,4) ans[i] = pay[i];
        ans[4] = totalHaveCoin;
        ans[5] = totalPayCoin;
    }
}

