void solve(int n) {
    vector<int> cnum(4);
    vector<int> ans(4, 0);
    vector<int> csum(4, 0);

    int coinnum = 0, sum = 0;
    for(int i=0; i<4; ++i) {
        cin >> cnum[i];
        coinnum += cnum[i];
        csum[i] = cnum[i] * coin[i];
        sum += csum[i];
    }

    int ret = sum - n;
    for(int i=3; i>=0; --i) {
        int buf = ret / coin[i];
        ret %= coin[i];
        ans[i] = cnum[i] - buf;
    }
    for(int i=0; i<4; ++i)
        if(ans[i]>0) cout << coin[i] << " " << ans[i] << endl;
}

