struct Wal{
    vlai c;
    Wal(){c=vlai(0,4);}
    Wal(vlai a){c=a;}
    void print(){
        if(c[0])cout<<"10 "<<c[0]<<endl;
        if(c[1])cout<<"50 "<<c[1]<<endl;
        if(c[2])cout<<"100 "<<c[2]<<endl;
        if(c[3])cout<<"500 "<<c[3]<<endl;
    }
};

int main(void){
    int n,h=0;
    vector<vlai> oturi(1,{0,0,0,0});
    rep(i,66*20){
        auto nxt=oturi.back();
        nxt[0]++;
        if(nxt[0]==5){
            nxt[0]=0,nxt[1]++;
            if(nxt[1]==2){
                nxt[1]=0,nxt[2]++;
                if(nxt[2]==5){
                    nxt[2]=0,nxt[3]++;
                }
            }
        }
        oturi.push_back(nxt);
    }
    while(cin>>n,n){
        n/=10;
        Wal p;
        vlai r={1,5,10,50};
        cin>>p.c[0]>>p.c[1]>>p.c[2]>>p.c[3];
        vector<Wal> val;
        rep(i,p.c[3]+1){
            rep(j,p.c[2]+1){
                rep(k,p.c[1]+1){
                    rep(l,p.c[0]+1){
                        Wal q({l,k,j,i});
                        int d=(q.c*r).sum()-n;
                        if(d<0)continue;
