                        //if(ok){q.print();cout<<(q.c*r).sum()<<" "<<n<<" "<<d<<endl<<oturi[d].sum()<<"====="<<endl;}
                    }
                }
            }
        }
        sort(all(val),[p](Wal a, Wal b){ return (p.c-a.c).sum()<(p.c-b.c).sum();});
        if(h++)cout<<endl;
        val.front().print();
    }
}

