const int coins[] = {10, 50, 100, 500};
int use[4], cnt[4];

void solve(void){
    int N;
    while (1) {
        cin >> N;
        if (N == 0) break;
        rep(4, i) cin >> cnt[i];

        int res = -N;
        rep(4, i) res += cnt[i]*coins[i];

        repr(4, i) {
            use[i] = max(0, cnt[i]-res/coins[i]);
            res -= (cnt[i]-use[i])*coins[i];
        }
        rep(4, i) {
            if (use[i]) printf("%d %d\n", coins[i], use[i]);
        }

        printf("\n");
    }
}

int main(void){
  solve();
  return 0;
}

