#include <iostream>

using namespace std;

#define INF 1<<28

int p;
int n[4];
int v[4] = {10, 50, 100, 500};

int count_coin(int a, int b, int c, int d) {
    int sum = v[0]*a + v[1]*b + v[2]*c + v[3]*d - p;
    if (sum < 0) return INF;
    int change = 0;
    for (int i = 3; i >= 0; i--) {
        int t = sum /v[i];
        sum -= t * v[i];
        change += t;
        if (sum == 0) break;
    }
    return change + a + b + c + d;
}

int main() {
    bool first = true;
    while (cin >> p, p != 0) {
        for (int i = 0; i < 4; i++) cin >> n[i];

        int min_coin = INF;
        int a, b, c, d;
        for (int i = 0; i <= n[0]; i++) 
            for (int j = 0; j <= n[1]; j++) 
                for (int k = 0; k <= n[2]; k++) 
                    for (int l = 0; l <= n[3]; l++) {
                        int t = count_coin(i, j, k, l);
                        if (min_coin > t) {
                            min_coin = t;
                            a = i, b = j, c = k, d = l;
                        }
                    }

        if (!first) cout << endl;
        else first = false;
        if (a != 0) 
            cout << v[0] << ' ' << a << endl;
        if (b != 0) 
            cout << v[1] << ' ' << b << endl;
        if (c != 0) 
            cout << v[2] << ' ' << c << endl;
        if (d != 0) 
            cout << v[3] << ' ' << d << endl;
    }
    return 0;
}

