#include <bits/stdc++.h>

using namespace std;

int bill;
int c[4], csum;

vector<int> calcChange(int p) {
	vector<int> ret(4, 0);
	ret[3] = p / 500; p %= 500;
	ret[2] = p / 100; p %= 100;
	ret[1] = p / 50;  p %= 50;
	ret[0] = p / 10;  p %= 10;
	return ret;
}

void solve() {
	int mincnt = 1000;
	vector<int> ans(4);

	for (int i = 0; i <= c[0]; i++) {
		for (int j = 0; j <= c[1]; j++) {
			for (int k = 0; k <= c[2]; k++) {
				for (int l = 0; l <= c[3]; l++) {
					int p = 10 * i + 50 * j + 100 * k + 500 * l;
					if (p < bill) continue;

					vector<int> change = calcChange(p - bill);
					if (i > 0 && change[0] > 0) continue;
					if (j > 0 && change[1] > 0) continue;
					if (k > 0 && change[2] > 0) continue;
					if (l > 0 && change[3] > 0) continue;

					int sum = change[0] + change[1] + change[2] + change[3];
					int aft = csum - (i + j + k + l) + sum;

					if (mincnt > aft) {
						mincnt = aft;
						ans[0] = i;
						ans[1] = j;
						ans[2] = k;
						ans[3] = l;
					}
				}
			}
		}
	}

	if (ans[0] > 0) cout << 10 << " " << ans[0] << endl;
	if (ans[1] > 0) cout << 50 << " " << ans[1] << endl;
	if (ans[2] > 0) cout << 100 << " " << ans[2] << endl;
	if (ans[3] > 0) cout << 500 << " " << ans[3] << endl;
}

int main() {
	while (1) {
		cin >> bill; if (bill == 0) break;
		cin >> c[0] >> c[1] >> c[2] >> c[3];
		csum = c[0] + c[1] + c[2] + c[3];
		solve();
		cout << endl;
	}

	return 0;
}

